module.exports = function(obj)
{
    var _target;
    var targetIsKey = false;
    var _this = this;

    this.of = function(target)
    {
        _target = target;
        targetIsKey = true;

        return _this;
    }

    this.to = function(value)
    {
        if(targetIsKey)
        {
            if(_target[obj] === undefined)
            {
                _target[obj] = value;
            }
        }
        else
        {
            if(obj === undefined)
            {
                obj = value;
            }
            else
            {
                var prop;

                for(prop in value)
                {
                    if(obj[prop] === undefined)
                    {
                        obj[prop] = value[prop];
                    }
                }
            }

            return obj;
        }
    };

    return this;
};
